    def load_data(self):
        controller = DonhangController()
        data = controller.getAllChitietdonhang()
        model = QtGui.QStandardItemModel(len(data), 7)
        model.setHorizontalHeaderLabels(["id","Id Đơn Hàng", "Id Sản Phẩm", "Số Lượng", "Đơn Giá", "Thành Tiền", "Ngày bán"])
        for row, item in enumerate(data):
            model.setItem(row, 0, QtGui.QStandardItem(str(item[0])))
            model.setItem(row, 1, QtGui.QStandardItem(str(item[1])))
            model.setItem(row, 2, QtGui.QStandardItem(str(item[2])))
            model.setItem(row, 3, QtGui.QStandardItem(str(item[3])))
            model.setItem(row, 4, QtGui.QStandardItem(str(item[4])))
