    def addChiTietDonHang(self):
        if self.txtiddonhang.toPlainText() == "" or self.idsanpham.toPlainText() == "" or self.soluong.toPlainText() == "" or self.dongia.toPlainText() == "":
            QtWidgets.QMessageBox.warning(None, "Lỗi", "Vui lòng nhập đầy đủ thông tin")
            return
        else:
            iddonhang = int(self.txtiddonhang.toPlainText())
            idsanpham = int(self.idsanpham.toPlainText())
            soluong = int(self.soluong.toPlainText())
            dongia = int(self.dongia.toPlainText())
            controller = DonhangController()
            controller.addChiTietDonHang(iddonhang,idsanpham, soluong, dongia)
            controller.updateTongtiendonhang(iddonhang)
            self.load_data()

    def updateChiTietDonHang(self):
        row = self.tblDuLieu.currentIndex().row()
        if row == -1:
            QtWidgets.QMessageBox.warning(None, "Lỗi", "Vui lòng chọn dòng cần cập nhật")
            return

        # Assuming the first column is ID, second is ID Đơn Hàng, etc.
