        idDonHang = self.tblDuLieu.model().data(self.tblDuLieu.model().index(row, 1))
        idSanPham = self.tblDuLieu.model().data(self.tblDuLieu.model().index(row, 2))
        soLuong = self.tblDuLieu.model().data(self.tblDuLieu.model().index(row, 3))
        donGia = self.tblDuLieu.model().data(self.tblDuLieu.model().index(row, 4))

        from Capnhatchitietdonhang import Ui_Dialog as capnhat
        self.dialog = QtWidgets.QDialog()
        self.ui = capnhat()
        self.ui.setupUi(self.dialog)

        # Set the retrieved data to the dialog's fields
