        self.ui.txtIDDonhang.setText(str(idDonHang))
        self.ui.txtIDsanpham.setText(str(idSanPham))
        self.ui.txtSoluong.setText(str(soLuong))
        self.ui.txtDongia.setText(str(donGia))

        self.dialog.show()

