    def deleteChiTietDonHang(self):
        row = self.tblDuLieu.currentIndex().row()
        if row == -1:
            QtWidgets.QMessageBox.warning(None, "Lỗi", "Vui lòng chọn dòng cần xoá")
            return
        else:
            item = self.tblDuLieu.model().index(row, 0)
            idsanpham = self.tblDuLieu.model().index(row, 2)
            soluong = self.tblDuLieu.model().index(row, 3)
            if item is not None:
                id = self.tblDuLieu.model().data(item)
                idsanpham = self.tblDuLieu.model().data(idsanpham)
                soluong = self.tblDuLieu.model().data(soluong)
                controller = DonhangController()
                controller.deleteChitietDonhang(id,idsanpham, soluong)
            self.load_data()


